customCSS:
#preview-content, [data-testid="preview-content"] {
  max-width: 72ch;
  margin-left: auto;
  margin-right: auto;
  font-variant-numeric: proportional-nums;
}
/* Un-style code blocks inside <pre> so they inherit the parent's style */
pre > code {
  background: transparent;
  padding: 0;
  border-radius: 0;
  font-size: 1em;
}
